package com.dl.one;

public class Eg2 {
	
	//static variable
	static int a=10;
	static int b=20;
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(a);
		System.out.println(b);
		
		//ClassName.VariableName
		System.out.println(Eg2.a);
		System.out.println(Eg2.b);
	}	

}
